    layerCanvas.width = width;
    layerCanvas.height = height;
    
    return {
      canvas: layerCanvas,
      ctx: layerCanvas.getContext('2d'),
      clear: () => layerCanvas.getContext('2d').clearRect(0, 0, width, height),
      render: (x = 0, y = 0) => {
        this.ctx.drawImage(layerCanvas, x, y);
      }
    };
