// stores/cart.js
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'

export const useCartStore = defineStore('cart', () => {
  // 
  const items = ref([])
  const couponCode = ref('')
  const discount = ref(0)
  
  // 
  const itemCount = computed(() => 
    items.value.reduce((total, item) => total + item.quantity, 0)
  )
  
  const subtotal = computed(() => 
    items.value.reduce((total, item) => total + item.price * item.quantity, 0)
  )
  
  const total = computed(() => {
    const subtotalAmount = subtotal.value
    const discountAmount = subtotalAmount * (discount.value / 100)
    return subtotalAmount - discountAmount
  })
  
  const isEmpty = computed(() => items.value.length === 0)
  
  // 
  function addItem(product) {
    const existingItem = items.value.find(item => item.id === product.id)
    
    if (existingItem) {
      existingItem.quantity++
    } else {
      items.value.push({
        ...product,
        quantity: 1
      })
    }
  }
  
  function removeItem(productId) {
    const index = items.value.findIndex(item => item.id === productId)
    if (index !== -1) {
      items.value.splice(index, 1)
    }
  }
  
  function updateItemQuantity(productId, quantity) {
    const item = items.value.find(item => item.id === productId)
    if (item) {
      item.quantity = quantity
    }
  }
  
  function clearCart() {
    items.value = []
    couponCode.value = ''
    discount.value = 0
  }
  
  async function applyCoupon(code) {
    try {
      const response = await fetch('https://api.example.com/coupons/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ code })
      })
      
      const data = await response.json()
      
      if (data.valid) {
        couponCode.value = code
        discount.value = data.discount
        return { success: true, discount: data.discount }
      } else {
        return { success: false, message: data.message }
      }
    } catch (error) {
      return { success: false, message: 'Failed to validate coupon' }
    }
  }
  
  async function checkout() {
    try {
      const response = await fetch('https://api.example.com/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          items: items.value,
          couponCode: couponCode.value,
          total: total.value
        })
      })
      
      const data = await response.json()
      
      if (data.success) {
        clearCart()
        return { success: true, orderId: data.orderId }
      } else {
        return { success: false, message: data.message }
      }
    } catch (error) {
      return { success: false, message: 'Checkout failed' }
    }
  }
  
  return {
    // 
    items,
    couponCode,
    discount,
    
    // 
    itemCount,
    subtotal,
    total,
    isEmpty,
    
    // 
    addItem,
    removeItem,
    updateItemQuantity,
    clearCart,
    applyCoupon,
    checkout
  }
})
