// slice
import { createSlice } from '@reduxjs/toolkit'

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: {},
    totalItems: 0,
    totalPrice: 0,
    checkoutStatus: 'idle' // 'idle', 'loading', 'success', 'error'
  },
  reducers: {
    addToCart: (state, action) => {
      const { id, name, price } = action.payload
      if (state.items[id]) {
        state.items[id].quantity += 1
      } else {
        state.items[id] = { id, name, price, quantity: 1 }
      }
      
      // 
      cartSlice.caseReducers.calculateTotals(state)
    },
    
    removeFromCart: (state, action) => {
      const id = action.payload
      delete state.items[id]
      cartSlice.caseReducers.calculateTotals(state)
    },
    
    updateQuantity: (state, action) => {
      const { id, quantity } = action.payload
      if (quantity <= 0) {
        delete state.items[id]
      } else {
        state.items[id].quantity = quantity
      }
      cartSlice.caseReducers.calculateTotals(state)
    },
    
    calculateTotals: (state) => {
      let totalItems = 0
      let totalPrice = 0
      
      Object.values(state.items).forEach(item => {
        totalItems += item.quantity
        totalPrice += item.price * item.quantity
      })
      
      state.totalItems = totalItems
      state.totalPrice = totalPrice
    },
    
    clearCart: (state) => {
      state.items = {}
      state.totalItems = 0
      state.totalPrice = 0
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(checkout.pending, (state) => {
        state.checkoutStatus = 'loading'
      })
      .addCase(checkout.fulfilled, (state) => {
        state.checkoutStatus = 'success'
        cartSlice.caseReducers.clearCart(state)
      })
      .addCase(checkout.rejected, (state) => {
        state.checkoutStatus = 'error'
      })
  }
})

export const { addToCart, removeFromCart, updateQuantity } = cartSlice.actions
export default cartSlice.reducer

// action
export const checkout = createAsyncThunk(
  'cart/checkout',
  async (cartItems, { rejectWithValue }) => {
    try {
      const response = await fetch('/api/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ items: Object.values(cartItems) })
      })
      
      if (!response.ok) {
        throw new Error('Checkout failed')
      }
      
      return await response.json()
    } catch (error) {
      return rejectWithValue(error.message)
    }
  }
)

