function ResponsiveImage({ src, alt, sizes, className }) {
  const [currentSrc, setCurrentSrc] = useState('')
  
  useEffect(() => {
    const updateSrc = () => {
      const width = window.innerWidth
      
      if (width < 768 && src.mobile) {
        setCurrentSrc(src.mobile)
      } else if (width < 1024 && src.tablet) {
        setCurrentSrc(src.tablet)
      } else {
        setCurrentSrc(src.desktop)
      }
    }
    
    updateSrc()
    window.addEventListener('resize', updateSrc)
    
    return () => window.removeEventListener('resize', updateSrc)
  }, [src])
  
  return (
    <img
      src={currentSrc}
      alt={alt}
      sizes={sizes}
      className={className}
    />
  )
}

// Intersection Observer
function IntersectionObserverImage({ src, alt, className }) {
  const [isLoaded, setIsLoaded] = useState(false)
  const [isInView, setIsInView] = useState(false)
  const imgRef = useRef()
  
  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        if (entries[0].isIntersecting) {
          setIsInView(true)
          observer.disconnect()
        }
      },
      { threshold: 0.1 }
    )
    
    if (imgRef.current) {
      observer.observe(imgRef.current)
    }
    
    return () => observer.disconnect()
  }, [])
  
  return (
    <div ref={imgRef} className={className}>
      {isInView && (
        <img
          src={src}
          alt={alt}
          onLoad={() => setIsLoaded(true)}
          style={{ opacity: isLoaded ? 1 : 0 }}
        />
      )}
    </div>
  )
}
