class ProxySandbox {
  constructor() {
    this.sandbox = {};
    this.proxy = null;
    this.active = false;
  }

  // 
  createProxy() {
    const that = this;
    
    this.proxy = new Proxy(window, {
      get(target, prop) {
        // 
        if (that.active && that.sandbox.hasOwnProperty(prop)) {
          return that.sandbox[prop];
        }
        
        // window
        return target[prop];
      },
      
      set(target, prop, value) {
        if (that.active) {
          // 
          that.sandbox[prop] = value;
          return true;
        } else {
          // window
          target[prop] = value;
          return true;
        }
      }
    });
    
    return this.proxy;
  }

  // 
  active() {
    this.active = true;
  }

  // 
  inactive() {
    this.active = false;
  }
}

